val input = ""

val result = input.zipWithIndex.foldLeft(Seq('n', 'd', 'z')) { (charSeq, acc) =>
  val (char, position) = acc
  if ((charSeq.toSet + char).size == 4) {
    println(s"Success at iteration ${position + 4}")
    char +: charSeq.dropRight(1)
    throw new RuntimeException("oops")
  } else {
    char +: charSeq.dropRight(1)
  }
}

val messageInput = ""

val messageResult = messageInput.zipWithIndex.foldLeft("zdnnfgfsgffgl".toCharArray.toSeq.reverse) { (charSeq, acc) =>
  val (char, position) = acc
  if ((charSeq.toSet + char).size == 14) {
    println(s"Message result success at iteration ${position + 14}")
    char +: charSeq.dropRight(1)
    throw new RuntimeException("oops")
  } else {
    char +: charSeq.dropRight(1)
  }
}
